  if (email === '' && password === '') {
    //Alert.alert('Campos vacíos', 'Ingresa tu correo y contraseña', [{ style: 'destructive' }])
    setError(true);
  } else {
    setError(false);
    setIsLoading(true);
    firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((res) => {
        console.log(res.user)
        showName= res.user.displayName
        showEmail= res.user.email
        console.log('User logged-in successfully!')
        getUser(showEmail, history, setIsLoading, setError, setAnswer, dispatch);
      })
      .catch((error => {
        setError(true);
        setIsLoading(false);
        setAnswer(true);
          console.log(error)
      }));
  }
}

//Registry

