import React from "react";
import firebase from '../components/database/firebase';
import { config } from "../components/config/config";

var UserStateContext = React.createContext();
var UserDispatchContext = React.createContext();
var showName, showIdentification;
function userReducer(state, action) {
  switch (action.type) {
    case "LOGIN_SUCCESS":
      return { ...state, isAuthenticated: true };
    case "SIGN_OUT_SUCCESS":
      return { ...state, isAuthenticated: false };
    default: {
      throw new Error(`Unhandled action type: ${action.type}`);
    }
  }
}

function UserProvider({ children }) {
  var [state, dispatch] = React.useReducer(userReducer, {
    isAuthenticated: !!localStorage.getItem("id_token"),
  });

  return (
    <UserStateContext.Provider value={state}>
      <UserDispatchContext.Provider value={dispatch}>
        {children}
      </UserDispatchContext.Provider>
    </UserStateContext.Provider>
  );
}

function useUserState() {
  var context = React.useContext(UserStateContext);
  if (context === undefined) {
    throw new Error("useUserState must be used within a UserProvider");
  }
  return context;
}

function useUserDispatch() {
  var context = React.useContext(UserDispatchContext);
  if (context === undefined) {
    throw new Error("useUserDispatch must be used within a UserProvider");
  }
  return context;
}

export { UserProvider, useUserState, useUserDispatch, userLogin, userRegistry, userRegistrySave,signOut, showName, showIdentification};

// ###########################################################

function userLogin (dispatch, email, password, history, setIsLoading, setError, setAnswer) {
  if (email === '' && password === '') {
    //Alert.alert('Campos vacíos', 'Ingresa tu correo y contraseña', [{ style: 'destructive' }])
    setError(true);
  } else {
    setError(false);
    setIsLoading(true);
    firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((res) => {
        console.log(res.user)
        showName= res.user.displayName
        showIdentification= res.user.uid
        console.log('User logged-in successfully!')
        setError(null)
        setIsLoading(false)
        dispatch({ type: 'LOGIN_SUCCESS' })
        history.push('/app/dashboard')
      })
      .catch((error => {
        setError(true);
        setIsLoading(false);
        setAnswer(true);
          console.log(error)
      }));
  }
}

//Registry

function userRegistry(history){
  history.push('/registry')
}

//function userRegistrySave(history, name,lastname,email,password,){
async function userRegistrySave ( identification,name, lastname, email,password, password2, history,setIsLoading,setError, setAnswer ){
  try {
    setError(false);
    setIsLoading(true);
    let response = await fetch(
      `${config.APIFireBase}/api/user`, {
        crossDomain:true,
        method: 'POST',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          "Access-Control-Allow-Origin": "*",
          "Access-Control-Allow-Methods": "DELETE, POST, GET, OPTIONS",
        },

        body: JSON.stringify({
          id: identification,
          name: name,
          lastName: lastname,
          email: email,
          password: password
        })
      },
      { mode: "cors" }
    );
    let json = await response;
    responseApi(json, setIsLoading,setError, setAnswer,history);
  } catch (error) {
    setError(true);
    setAnswer(true);
    console.error(error);
  }
};

function responseApi(res, setIsLoading,setError, setAnswer, history) {
  console.log('esto es la respuesta',res.description, res.json())
  if (typeof res.error!== 'undefined'){
    setIsLoading(false);
    setError(true);
    console.log('Upps!', res.error)            
  }else{
    if(typeof res.status === 'undefined' || res.status!==200){
      setIsLoading(false);
      setError(true);
      setAnswer(true);
      console.log(JSON.stringify(res))
      console.log('Upps!', 'Algo pasó, intenta de nuevo')      
    }  else{
      if(res.status==200){
        setError(null);
        setIsLoading(false);
        history.push('/app/dashboard')
        console.log('respuesta!!!!!!!!!', res)                   
      } else{
        setError(true);
        setIsLoading(false)
        setAnswer(true);
        console.log('Upps!', 'Algo pasó intenta de nuevo', res)          
      }
    }    
  }

}

function loginUser(dispatch, login, password, history, setIsLoading, setError) {
  setError(false);
  setIsLoading(true);
  if (!!login && !!password) {
    setTimeout(() => {
      localStorage.setItem('id_token', 1)
      setError(null)
      setIsLoading(false)
      dispatch({ type: 'LOGIN_SUCCESS' })

      history.push('/app/dashboard')
    }, 2000);
  } else {
    dispatch({ type: "LOGIN_FAILURE" });
    setError(true);
    setIsLoading(false);
  }
}

function signOut(dispatch, history) {
  localStorage.removeItem("id_token");
  dispatch({ type: "SIGN_OUT_SUCCESS" });
  history.push("/login");
}
