import React from "react";
import firebase from "../components/database/firebase";
import { config } from "../components/config/config";

var UserStateContext = React.createContext();
var UserDispatchContext = React.createContext();
var showName, showIdentification, showEmail, showDependence, showDependenceName,showTypeUser, showCampus;
var dependence, goods, type;
function userReducer(state, action) {
  switch (action.type) {
    case "LOGIN_SUCCESS":
      return { ...state, isAuthenticated: true };
    case "SIGN_OUT_SUCCESS":
      return { ...state, isAuthenticated: false };
    default: {
      throw new Error(`Unhandled action type: ${action.type}`);
    }
  }
}

function UserProvider({ children }) {
  var [state, dispatch] = React.useReducer(userReducer, {
    isAuthenticated: !!localStorage.getItem("id_token"),
  });

  return (
    <UserStateContext.Provider value={state}>
      <UserDispatchContext.Provider value={dispatch}>
        {children}
      </UserDispatchContext.Provider>
    </UserStateContext.Provider>
  );
}

function useUserState() {
  var context = React.useContext(UserStateContext);
  if (context === undefined) {
    throw new Error("useUserState must be used within a UserProvider");
  }
  return context;
}

function useUserDispatch() {
  var context = React.useContext(UserDispatchContext);
  if (context === undefined) {
    throw new Error("useUserDispatch must be used within a UserProvider");
  }
  return context;
}

export {
  UserProvider,
  useUserState,
  useUserDispatch,
  userLogin,
  userRegistry,
  userRegistrySave,
  signOut,
  showName,
  showIdentification,
  dependence,
  showEmail,
  showDependence,
  showDependenceName,
  saveGood,
  showCampus,
  goods
};

// -----٩(˘◡˘)۶------

function userLogin(
  dispatch,
  email,
  password,
  history,
  setIsLoading,
  setError,
  setAnswer
) {
  if (email === "" && password === "") {
    //Alert.alert('Campos vacíos', 'Ingresa tu correo y contraseña', [{ style: 'destructive' }])
    setError(true);
  } else {
    setError(false);
    setIsLoading(true);
    firebase
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((res) => {
        console.log(res.user);
        showName = res.user.displayName;
        showEmail = res.user.email;
        console.log("User logged-in successfully!");
        getUser(
          showEmail,
          history,
          setIsLoading,
          setError,
          setAnswer,
          dispatch
        );
      })
      .catch((error) => {
        setError(true);
        setIsLoading(false);
        setAnswer(true);
        console.log(error);
      });
  }
}

