import React, { useState } from "react";
import {
  Paper,
  TextField,
  Button,
  Avatar,
  CircularProgress,
  ListItem,
  ListItemText,
  Menu,
} from "@material-ui/core";
import { useTheme } from "@material-ui/styles";
import Alert from "@material-ui/lab/Alert";
import CloseIcon from "@material-ui/icons/Close";
import IconButton from "@material-ui/core/IconButton";
import Collapse from "@material-ui/core/Collapse";
// styles
import useStyles from "./styles";

// components
import { Typography } from "../../components/Wrappers";
import { goods } from "../../context/UserContext";
import {
  showIdentification,
  showName,
  showDependence,
  saveGood,
} from "../../context/UserContext";
import { FixedSizeList } from "react-window";




export default function Dashboard(props) {
  var classes = useStyles();
  var [emailValue, setEmailValue] = useState("");
  var [isLoading, setIsLoading] = useState(false);
  var [descriptionValue, setDescriptionValue] = useState("");
  var [plateValue, setPlateValue] = useState("");
  var [placeValue, setPlaceValue] = useState("");
  var [physicalspaceValue, setPhysicalspaceValue] = useState("");
  var [brandValue, setBrandValue] = useState("");
  var [stateValue, setStateValue] = useState("");
  var [reasonValue, setReasonValue] = useState("");
  var [answer, setAnswer] = useState(null);
  var [error, setError] = useState(null);
  const [open, setOpen] = React.useState(true);
  const [dependencia, setDependencia] = React.useState("");


  const listItems = goods.map((number) =>
  <li>{number.name}</li>
);

  return (
    <>
      <Paper
        variant="elevation"
        elevation={5}
        className={classes.principalContainer}
        square={false}
      >
        <div className={classes.formContainer}>
          <div className={classes.form}>
            <Typography variant="h1" className={classes.title}>
              Nuevo inventario
            </Typography>
            <TextField
              id="dependence"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              disabled="true"
              margin="normal"
              placeholder={showDependence}
              label={showDependence}
              variant="outlined"
            />
            <TextField
              id="name"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              disabled="true"
              margin="normal"
              placeholder={showName}
              label={showName}
              variant="outlined"
            />
            <TextField
              id="identification"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              disabled="true"
              margin="normal"
              placeholder="Número de identificación"
              label={showIdentification}
              variant="outlined"
            />
            <div className={classes.list}>
            <ul>{listItems}</ul>
            </div>
            <Typography variant="h2" className={classes.title}>
              Bienes a registrar
            </Typography>
            <TextField
              id="nameGood"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={namegoodValue}
              onChange={(e) => setNamegoodValue(e.target.value)}
              label="Descripción"
              placeholder="Descripción"
              variant="outlined"
            />
            <TextField
              id="descripcion"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={descriptionValue}
              onChange={(e) => setDescriptionValue(e.target.value)}
              label="Descripción"
              placeholder="Descripción"
              variant="outlined"
            />
            <TextField
              id="placa"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={plateValue}
              onChange={(e) => setPlateValue(e.target.value)}
              label="Placa"
              placeholder="Placa"
              variant="outlined"
            />
            <TextField
              id="Sede"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={placeValue}
              onChange={(e) => setPlaceValue(e.target.value)}
              label="Sede"
              placeholder="Sede"
              variant="outlined"
            />
            <TextField
              id="Espacio"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={physicalspaceValue}
              onChange={(e) => setPhysicalspaceValue(e.target.value)}
              label="Espacio Físico"
              placeholder="Espacio Físico"
              variant="outlined"
            />
            <TextField
              id="Marca"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={brandValue}
              onChange={(e) => setBrandValue(e.target.value)}
              label="Marca y Serie"
              placeholder="Marca y Serie"
              variant="outlined"
            />
            <TextField
              id="Estado"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={stateValue}
              onChange={(e) => setStateValue(e.target.value)}
              label="Estado"
              placeholder="Estado"
              variant="outlined"
            />
            <TextField
              id="Motivo"
              InputProps={{
                classes: {
                  underline: classes.textFieldUnderline,
                  input: classes.textField,
                },
              }}
              value={reasonValue}
              onChange={(e) => setReasonValue(e.target.value)}
              label="Motivo Ingreso"
              placeholder="Motivo Ingreso"
              variant="outlined"
            />
            {isLoading ? (
              <CircularProgress size={26} className={classes.loginLoader} />
            ) : (
              <Button
                disabled={
                  descriptionValue === "" ||
                  plateValue === "" ||
                  placeValue === "" ||
                  physicalspaceValue === "" ||
                  brandValue === "" ||
                  stateValue === "" ||
                  reasonValue === ""
                }
                onClick={() => {
                  saveGood(
                    descriptionValue,
                    plateValue,
                    placeValue,
                    physicalspaceValue,
                    brandValue,
                    stateValue,
                    reasonValue,
                    props.history,
                    setIsLoading,
                    setError,
                    setAnswer
                  );
                  alert("Datos enviados");
                }}
                variant="contained"
                color="secondary"
                className={classes.addButton}
                startIcon={
                  <Avatar
                    src="https://www.freeiconspng.com/thumbs/check-tick-icon/tick-icon-30.png"
                    className={classes.iconC}
                  />
                }
              ></Button>
            )}
          </div>
        </div>
        {answer ? (
          <Collapse in={open}>
            <Alert
              severity="error"
              action={
                <IconButton
                  aria-label="close"
                  color="inherit"
                  size="small"
                  onClick={() => {
                    setOpen(false);
                  }}
                >
                  <CloseIcon fontSize="inherit" />
                </IconButton>
              }
            >
              Algó pasó verifica los datos
            </Alert>
          </Collapse>
        ) : (
          <Typography
            color="secondary"
            className={classes.errorMessage}
          ></Typography>
        )}
      </Paper>
    </>
  );
}
