import { makeStyles } from "@material-ui/styles";
import Image from '../../assets/images/brackground.jpeg';

export default makeStyles((theme) => ({
  container: {
    alignItems: "center",
    justifyContent: "center",
    marginLeft: "30%",
    width: "40%",
    marginTop: "10%",
    direction: "row",
    borderRadius: 35,
    backgroundImage: `url(${Image})`
  },
  errorMessage: {
    alignItems: "center",
    direction: "row",

  },
  title: {
    color: "#046FC3",
    fontSize: 25,
    textAlign: "center",
    fontWeight: "bold",
    fontColor: "#ffff"
  },
  textFieldUnderline: {
    borderRadius: 25,
    "&:before": {
      borderBottomColor: "#fff",
    },
    "&:after": {
      borderBottomColor: "#000",
    },
    "&:hover:before": {
      borderBottomColor: "#ff7000 !important",
    },
  },
  textField: {
    borderBottomColor: "#fff",
  },
  formButtons: {
    width: "100%",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  loginButtom: {
    borderRadius: 25,
    backgroundColor: "#046FC3",
    marginLeft: "40%",
  },
  forgetButton: {
    textTransform: "none",
    color: "#000",
    fontWeight: 400,
    margin: "auto",
    textAlign: "center",
    borderRadius: 25,
  },

  errorMessage: {
    textAlign: "center",
  },
  logotypeImage: {
    marginTop: "4%",
    marginLeft: "37%",
    width: "25%",
    height: "20%",
  },
  loginBackground: {
    variant: "outlined",
    justifyContent: "center",
    minheight: "5%",
    padding: "15%",
    backgroundImage: `url(${Image})`
  },
}));
